sys_script_include.script:
var RequestedItemHelper = Class.create();
RequestedItemHelper.prototype = {
    initialize: function() {
    },

	filterDeliverables: function(services){
		var filterCondition = "";
		var glideDeliverable = new GlideRecord("x_novrp_pls_deliverable");
		glideDeliverable.addQuery("parent", "IN", services);
		glideDeliverable.query();

		while(glideDeliverable.next()){
			filterCondition += glideDeliverable.getUniqueValue() + ",";
		}

		return "sys_idIN" + filterCondition;
	},

    type: 'RequestedItemHelper'
};
