sys_script_include.script:
var DemandHelper = Class.create();
DemandHelper.prototype = {
    initialize: function() {
    },

	getUsersFromGroup: function(sysId){
		var filter = "sys_idIN";
		var glideUserGroup = new GlideRecord("sys_user_grmember");
		glideUserGroup.addQuery("group", sysId);
		glideUserGroup.query();

		while(glideUserGroup.next()){
			filter += glideUserGroup.getValue("user") + ",";
		}

		return filter;
	},

	getBrandFilter: function(businessUnit){
		var filter = "sys_idIN";
		var glideBrand = new GlideRecord("x_novrp_pls_brand");
		glideBrand.addNotNullQuery("franchise");
		glideBrand.query();

		while(glideBrand.next()){
			var franchise = glideBrand.franchise;
			if(franchise.business_unit == businessUnit){
				filter += glideBrand.getUniqueValue() + ",";
			}
		}

		return filter;
	},

	getRegion: function(businessUnit, country){
		var glideRegionCountry = new GlideRecord("x_novrp_pls_region_country");
		glideRegionCountry.addQuery("country", country);
		glideRegionCountry.query();

		while(glideRegionCountry.next()){
			var region = glideRegionCountry.region;
			if(region && region.business_unit == businessUnit){
				return region;
			}
		}

		return "NULL";
	},

    type: 'DemandHelper'
};
