sys_ui_action.script:
//Client-side 'onclick' function
function generateActivities(){

	//Call the UI Action and skip the 'onclick' function
	gsftSubmit(null, g_form.getFormElement(), 'generate_activities'); //MUST call the 'Action name' set in this UI Action
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined'){
	runBusRuleCode();
}

//Server-side function
function runBusRuleCode(){
	generateTasks(current);
	action.setRedirectURL(current);
}

function generateTasks(request){
	var dateTimeHelper = new DateTimeHelper();
	var glideTask = new GlideRecord("x_novrp_pls_task_configuration");
	glideTask.addQuery("catalog_item", request.getValue("catalog_item"));
	glideTask.orderBy("order");
	glideTask.query();

	if(!glideTask.hasNext()){
		gs.addInfoMessage("No task configured for the subservice " + request.getDisplayValue("subservice"));
	}

	var currentMilestone = "";
	var start = request.planned_start_date;

	while(glideTask.next()){
		var glideServiceRequestTask = createTask(request, glideTask, start);
		start =  glideServiceRequestTask.planned_end_date;
	}
}

function createTask(top_task, template, start){
	var dateTimeHelper = new DateTimeHelper();
	var end = dateTimeHelper.calculateEndDate(
		top_task.planned_start_date,
		template.getValue("estimated_efforts_hours"));

	var glideServiceRequestTask = new GlideRecord("x_novrp_pls_sr_task");
	glideServiceRequestTask.newRecord();
	glideServiceRequestTask.top_task = top_task.getUniqueValue();
	glideServiceRequestTask.milestone = template.getValue("milestone");
	glideServiceRequestTask.order = template.getValue("order");
	glideServiceRequestTask.planned_start_date = start;
	glideServiceRequestTask.planned_end_date = end;
	glideServiceRequestTask.estimated_efforts_hours = template.getValue("estimated_efforts_hours");
	glideServiceRequestTask.short_description = template.getValue("description");
	glideServiceRequestTask.assignment_group = template.getValue("assignment_group");
	glideServiceRequestTask.insert();

	return glideServiceRequestTask;
}
