sys_ui_action.script:
function completeServiceRequest(){
	var cancel = false;

	g_form.hideAllFieldMsgs('error');	

	if(g_form.getValue("is_the_deliverable_accurate") == ""){
		g_form.setMandatory("is_the_deliverable_accurate", true);
		g_form.showFieldMsg("is_the_deliverable_accurate",
							"Please select if the deliverable was accurate or not",
							'error');
		cancel = true;
	}

	if(g_form.getValue("delivery_accuracy_level_percentage") == ""){
		g_form.setMandatory("delivery_accuracy_level_percentage", true);
		g_form.showFieldMsg("delivery_accuracy_level_percentage",
							"Please enter the level of accuracy before you complete the service request",
							'error');
		cancel = true;
	}

	if(cancel){
		g_tabs2Sections.setActive(0);
		return false;
	}

	if(g_form.getValue("delivery_accuracy_level_percentage") == 0 && 
	   !confirm("Are you sure you want the level of accuracy for the current service request is correct?"))
	{
		return false;
	}

	gsftSubmit(null, g_form.getFormElement(), 'complete_service_request');
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined'){
	runBusRuleCode();
}

//Server-side function
function runBusRuleCode(){
	current.actual_end_date = new GlideDate();
	current.state = 7;
	current.update();

	action.setRedirectURL(current);
}
